import { API_URL } from "../../component/config/api";
import axios from "axios";


export function registerUser({ userdata, navigate }) {
    console.log("userdata", userdata);
    return async function (dispatch) {
        try {
            dispatch({ type: 'auth/registerRequest' });



            if (!userdata) {
                throw new Error('No user data provided');
            }

            console.log('Registering user with data:', userdata);

            const { data } = await axios.post(`${API_URL}/auth/signup`, userdata);

            console.log('Registration success:', data);

            dispatch({ type: 'auth/registerSuccess', payload: data });

            // Optional: redirect after success
            if (navigate) {
                navigate('/account/login');
            }
        } catch (error) {
            console.error('Registration error:', error);

            dispatch({
                type: 'auth/registerFailure',
                payload: error.response?.data?.message || error.message || 'Registration failed',
            });
        }
    };
}
export function healthCheck() {
    return async function (dispatch) {
        dispatch({ type: "auth/registerRequest" })
        try {
            const { data } = await axios.get(`${API_URL}/auth/healthCheck`);
            console.log("get data : ", data)
            dispatch({ type: "auth/healthCheck", payload: data });

        } catch (error) {
            console.log(error)
        }
    }
}
