import { Avatar, Card, CardHeader, Chip, IconButton } from '@mui/material';
import React from 'react';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
// import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';


export default function RestaurantCard({data}) {



  return (
    <Card sx={{ maxWidth: 345, m: "1rem" }}>
      <CardHeader avatar={
        <Avatar sx={{
          bgcolor: "#e91e63"
        }}>
          Z
        </Avatar>
      }
        action={ 
          <IconButton>
            <MoreVertIcon/>
          </IconButton>
        } />
      <div
        // onClick={navigateToRestaurant}
        className={`${data.open ? 'cursor-pointer' : 'cursore-not-allowed'} relative`}>
        <img className='w-full h-[10rem] rounded-t-md'
          src={data.images[0]}
          alt="indian fast food" />
        <Chip size='small'
          className='absolute top-2 left-2'
          color={data.open ? "success" : "error"}
          label={data.open ? "Open" : "Closed"} />
      </div>

      <div className='p-4 textPart lg:flex w-full justify-between'>
        <div className='space-y-1'>
          <p className='font-semibold text-lg'>{data.name}</p>
          <p className='text-gray-500 text-sm'>
            {
              data.description.length > 40 ?
                data.description.substring(0, 40) + "..." : data.description
            }
          </p>
        </div>

        <div>
          <IconButton
            // onClick={handleAddToFavorites}
          >
            {true? <FavoriteIcon/> : <FavoriteBorderIcon/>}
          </IconButton>
        </div>
      </div>

    </Card>
  )
}
