export const reducers = {
    healthCheck: (state, action) => {
        state.isLoading = true;
        state.error = null;
        state.success = null;
        state.success = action.payload;

    },
    registerRequest: (state) => {
        state.isLoading = true;
        state.error = null;
        state.success = null;
    },
    loginRequest: (state) => {
        state.isLoading = true;
        state.error = null;
        state.success = null;
    },
    getUserRequest: (state) => {
        state.isLoading = true;
        state.error = null;
        state.success = null;
    },
    resetPasswordRequest: (state) => {
        state.isLoading = true;
        state.error = null;
        state.success = null;
    },
    requestResetPasswordRequest: (state) => {
        state.isLoading = true;
        state.error = null;
        state.success = null;
    },
    addToFavoriteRequest: (state, action) => {
        state.isLoading = true;
        state.error = null;
        state.success = null;
    },

    addToFavoriteSuccess: (state, action) => {
        state.isLoading = false;
        state.jwt = action.payload?.jwt;
        state.favorites = [...state.favorites, action.payload?.favorite];

    },

    registerSuccess: (state, action) => {
        state.isLoading = false;
        state.jwt = action?.payload;
        state.success = "Register Success";
    },
    // add to fav success

    loginSuccess: (state, action) => {
        state.isLoading = false;
        console.log(action.payload.jwt)
        state.jwt = action.payload?.jwt;
        state.success = "Login Success";
    },

    getUserSuccess: (state, action) => {
        state.isLoading = false;
        state.user = action.payload;

    },
    requestResetPasswordSuccess: (state, action) => {
        state.isLoading = false;
        state.success = action.payload.message;
    },
    registerFailure: (state, action) => {
        state.isLoading = false;
        state.error = action.payload;
    },
    loginFailure: (state, action) => {
        state.isLoading = false;
        state.error = action.payload;
    },
    getUserFailure: (state, action) => {
        state.isLoading = false;
        state.error = action.payload;
    },
    requestResetPasswordFailure: (state, action) => {
        state.isLoading = false;
        state.error = action.payload;
    },
    addFavoriteFailure: (state, action) => {
        state.isLoading = false;
        state.error = action.payload;
    },
    logout: (state) => {
        localStorage.removeItem("jwt");
        state.user = null;
        state.jwt = null;
        state.success = "logout success";
    }
};
