import { registerRequest, registerSuccess } from "./Reducer";
import { API_URL } from "../../component/config/api";
import axios from "axios";

export function registerUser(reqData) {
    return async function (dispatch) {
        // dispatch({ type: "REGISTER_REQUEST" });
        dispatch(registerRequest(reqData));
        try {

            const { data } =
                await axios.post(`${API_URL}/auth/signup`, reqData.userData);
            console.log("data", data);
            if (data.jwt) localStorage.setItem("jwt", data.jwt);
            if (data.role === "ROLE_RESTAUTANT_OWNER")
                reqData.navigate("/admin.restaurant");
            else
                reqData.navigate("/");
            dispatch(registerSuccess(data));
        } catch (error) {
            dispatch()
        }
    }
}

