import React from 'react';

import SearchIcon from '@mui/icons-material/Search';
import PersonIcon from '@mui/icons-material/Person';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import IconButton from '@mui/material/IconButton';
import { Avatar, Badge } from '@mui/material';
import './Navbar.css';
import { useNavigate } from 'react-router-dom';
import { pink } from '@mui/material/colors';
import { useSelector } from 'react-redux';
import { useState } from "react";


export default function Navbar() {
    const navigate = useNavigate();
    const { auth } = useSelector(store => store);
    // console.log("auth", auth);

    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);

    const handleOpenMenu = (event) => {
        setAnchorEl(event.currentTarget);
    }

    const handleCloseMenu = (event) => {
        setAnchorEl(event.currentTarget);
    }

    const navigateToProfile = (evene) => {
        auth.user?.role === "ROLE_ADMIN" || 
            auth.user?.role === "ROLE_RESTAURANT_OWNER"
    }

    const handleClick = () => {
        navigate("/");
    }

    const handleAvatarClick = () => {
        if (auth.user?.role === "ROLE_CUSTOMER")
            navigate("/my-profile");
        else
            navigate("/admin/restaurants");
    }

    return (
        <div className='navbar sticky top-[0] w-full  px-5 z-50 py-[.8rem]
             bg-[#e91e63] lg:px-20 flex justify-between' >

            <div className='lg:mr-10 cursor-pointer flex items-center space-x-4'>
                <div className='logo font-semibold text-grey-300 text-2xl' onClick={handleClick}>
                    CraveKart
                </div>
            </div>

            <div className='flex items-center space-x-2 lg:space-x-10'>
                <div>
                    <IconButton onClick={()=>navigate("/search")}>
                        <SearchIcon sx={{ fontSize: "1.5rem" }} />
                    </IconButton>
                </div>

                <div className="flex items-center space-x-2">    
                    {auth.user?.fullName ? (
                        <span
                            id="demo-positioned-button"
                            onClick={
                                auth.user?.role === "ROLE_ADMIN" ?
                                    handleOpenMenu : navigateToProfile
                            }
                            className="font-semibold cursor-pointer"
                        >

                            <Avatar sx={{ bgcolor: "white", color: pink.A400 }}
                            onClick= {handleAvatarClick}>
                                    {auth?.user?.fullName[0]}
                            </Avatar>
                        </span>
                        ) : (
                                <IconButton onClick={() => navigate("/account/login")}>
                                    <PersonIcon/>
                                </IconButton>
                            )
                        }

                    <IconButton className=''>
                        <Badge badgeContent={4} color="secondary">
                            <ShoppingCartIcon sx={{ fontSize: "1.5rem" }} />
                        </Badge>
                        </IconButton>
                </div>
            </div>

        </div>
    )
}
