import { createSlice } from "@reduxjs/toolkit";


const initialState = {
    user: null,
    isLoading: false,
    error: null,
    jwt: null,
    favorites: [],
    success: null,
}


const authSlice = createSlice({
    name: "auth",
    initialState,
    reducers: {
        registerRequest: (state) => {
            state.isLoading = true;
            state.error = null;
            state.success = null;
        },
        loginRequest: (state) => {
            state.isLoading = true;
            state.error = null;
            state.success = null;
        },
        getUserRequest: (state) => {
            state.isLoading = true;
            state.error = null;
            state.success = null;
        },
        resetPasswordRequest: (state) => {
            state.isLoading = true;
            state.error = null;
            state.success = null;
        },
        requestResetPasswordRequest: (state) => {
            state.isLoading = true;
            state.error = null;
            state.success = null;
        },
        registerSuccess: (state, action) => {
            state.isLoading = false;
            state.jwt = action.payload.jwt;
            state.success = "Register Success";
        },
        // add to fav success

        loginSuccess: (state, action) => {
            state.isLoading = false;
            state.jwt = action.payload?.jwt;
            state.success = "Login Success";
        },
        getUserSuccess: (state, action) => {
            state.isLoading = false;
            state.user = action.payload;

        },
        requestResetPasswordSuccess: (state, action) => {
            state.isLoading = false;
            state.success = action.payload.message;
        },
        registerFailure: (state, action) => {
            state.isLoading = false;
            state.error = action.payload;
        },
        loginFailure: (state, action) => {
            state.isLoading = false;
            state.error = action.payload;
        },
        getUserFailure: (state, action) => {
            state.isLoading = false;
            state.error = action.payload;
        },
        requestResetPasswordFailure: (state, action) => {
            state.isLoading = false;
            state.error = action.payload;
        },
        logout: (state) => {
            localStorage.removeItem("jwt");
            state.user = null;
            state.jwt = null;
            state.success = "logout success";
        }
    }
})

export const { registerRequest, loginRequest, getUserRequest, resetPasswordRequest, requestResetPasswordRequest,
    registerSuccess, loginSuccess } = authSlice.actions;

export const authReducer = authSlice.reducer;
